import styled from 'styled-components';

const Card = styled.div`
display: flex;
justify-content: center;
align-items: center;

@media (max-width:550px) {
    flex-direction: column;
}
`;

const H1 = styled.h1`
color: ${({ theme })=> theme.colors.primary}
text-align: center;

`;

export default function StepCard (){
  return (
      <Card>
          <h3>Titulo 1</h3>
          <p>Descrição</p>
      </Card>
  )
}
