display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
flex-shrink: 0;

`;

export default function HomePageHeaders (){

    const handleSubmit =  async ({name, email}) => {
    const{ NEXT_PUBLIC_API_URL } = process.env;
