        method: 'POST',
        body: JSON.stringify({
            name,
            email,

        })


    }) 
    }
return (
    <ImageContainer>
       <Container>
       <Logo />
       <h2>A melhor brincadeira do Natal </h2>
       </Container>
       <FormDiv>
           <NameEmailForm onSubmit={handleSubmit} />
       </FormDiv>
    </ImageContainer>
)
}
